package business.applicationservice.factory.test;import business.applicationservice.ApplicationServicePaziente;import business.applicationservice.exception.UnavaliableApplicationServiceException;import business.applicationservice.factory.ApplicationServiceFactory;import org.junit.After;import org.junit.Before;import org.junit.Test;import presentation.controller.ApplicationService;import static org.junit.Assert.assertTrue;import static org.junit.Assert.fail;public class ApplicationServiceFactoryTest {    @Before    public void setUp() throws Exception {    }    @After    public void tearDown() throws Exception {    }    @Test    public void testBuildIstance() {        String msg = null;        ApplicationService as = ApplicationServiceFactory.buildInstance("InserisciPaziente");        msg = as.getClass().getName() + " instead of " + "ApplicationServicePaziente";        assertTrue(msg, as instanceof ApplicationServicePaziente);        try {            ApplicationServiceFactory.buildInstance("UnknownCommand");            fail("UnavaliableApplicationServiceException not thrown");        } catch (UnavaliableApplicationServiceException e) {            assertTrue(true);        }    }}